func testCyclesUpdate(t *testing.T) {
	t.Parallel()

	if 0 == len(cyclePrimaryKeyColumns) {
		t.Skip("Skipping table with no primary key columns")
	}
	if len(cycleAllColumns) == len(cyclePrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &Cycle{}
	if err = randomize.Struct(seed, o, cycleDBTypes, true, cycleColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize Cycle struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := Cycles().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, cycleDBTypes, true, cyclePrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Cycle struct: %s", err)
	}

	if rowsAff, err := o.Update(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("should only affect one row but affected", rowsAff)
	}
}

func testCyclesSliceUpdateAll(t *testing.T) {
	t.Parallel()

	if len(cycleAllColumns) == len(cyclePrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}

	seed := randomize.NewSeed()
	var err error
	o := &Cycle{}
	if err = randomize.Struct(seed, o, cycleDBTypes, true, cycleColumnsWithDefault...); err != nil {
		t.Errorf("Unable to randomize Cycle struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Error(err)
	}

	count, err := Cycles().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}

	if count != 1 {
		t.Error("want one record, got:", count)
	}

	if err = randomize.Struct(seed, o, cycleDBTypes, true, cyclePrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Cycle struct: %s", err)
	}

	// Remove Primary keys and unique columns from what we plan to update
	var fields []string
	if strmangle.StringSliceMatch(cycleAllColumns, cyclePrimaryKeyColumns) {
		fields = cycleAllColumns
	} else {
		fields = strmangle.SetComplement(
			cycleAllColumns,
			cyclePrimaryKeyColumns,
		)
	}

	value := reflect.Indirect(reflect.ValueOf(o))
	typ := reflect.TypeOf(o).Elem()
	n := typ.NumField()

	updateMap := M{}
	for _, col := range fields {
		for i := 0; i < n; i++ {
			f := typ.Field(i)
			if f.Tag.Get("boil") == col {
				updateMap[col] = value.Field(i).Interface()
			}
		}
	}

	slice := CycleSlice{o}
	if rowsAff, err := slice.UpdateAll(ctx, tx, updateMap); err != nil {
		t.Error(err)
	} else if rowsAff != 1 {
		t.Error("wanted one record updated but got", rowsAff)
	}
}

func testCyclesUpsert(t *testing.T) {
	t.Parallel()

	if len(cycleAllColumns) == len(cyclePrimaryKeyColumns) {
		t.Skip("Skipping table with only primary key columns")
	}
	if len(mySQLCycleUniqueColumns) == 0 {
		t.Skip("Skipping table with no unique columns to conflict on")
	}

	seed := randomize.NewSeed()
	var err error
	// Attempt the INSERT side of an UPSERT
	o := Cycle{}
	if err = randomize.Struct(seed, &o, cycleDBTypes, false); err != nil {
		t.Errorf("Unable to randomize Cycle struct: %s", err)
	}

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()
	if err = o.Upsert(ctx, tx, boil.Infer(), boil.Infer()); err != nil {
		t.Errorf("Unable to upsert Cycle: %s", err)
	}

	count, err := Cycles().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}
	if count != 1 {
		t.Error("want one record, got:", count)
	}

	// Attempt the UPDATE side of an UPSERT
	if err = randomize.Struct(seed, &o, cycleDBTypes, false, cyclePrimaryKeyColumns...); err != nil {
		t.Errorf("Unable to randomize Cycle struct: %s", err)
	}

	if err = o.Upsert(ctx, tx, boil.Infer(), boil.Infer()); err != nil {
		t.Errorf("Unable to upsert Cycle: %s", err)
	}

	count, err = Cycles().Count(ctx, tx)
	if err != nil {
		t.Error(err)
	}
	if count != 1 {
		t.Error("want one record, got:", count)
	}
}
